INSERT INTO `wp_promag_email_templates` VALUES (1,'On Joining Group','Welcome to your new Group!','Dear {{first_name}} {{last_name}},<br /><br />You are now a member of {{group_name}} Group on {{site_name}}.<br /><br />Kind Regards.'),(2,'User Account Activated','User Account Activated','Dear {{first_name}} {{last_name}},<br /><br />Admin has activated your user account on {{site_name}}. You can now login and access your member\'s area. <br /><br />Kind Regards.'),(3,'User Account Deactivated','User Account Deactivated','Dear {{first_name}} {{last_name}},<br /><br />Admin has deactivated your user account on {{site_name}}. For now, you cannot login or access your member area.<br /><br />Kind Regards.'),(4,'Password Successfully Changed','Password Successfully Changed','Dear {{first_name}} {{last_name}},<br /><br />Password for your user account on {{site_name}} was successfully changed. If you did not changed it, please contact the Manager immediately.<br /><br />Kind Regards.'),(5,'User Account Deleted','User Account Deleted','Dear {{first_name}} {{last_name}},<br /><br />Your user account on {{site_name}} associated with username {{user_login}} has been deleted successfully.<br /><br />Kind Regards.'),(6,'New User Blog Post','Your Post was Published Successfully!','Dear {{first_name}} {{last_name}},<br /><br />Your blog post titled {{post_name}} was approved and published successfully.<br /><br />Kind Regards.'),(7,'Group Manager assignment','Congratulation! You have been made {{group_admin_label}} of {{group_name}}','Hello {{first_name}} {{last_name}},<br /><br />We wish to inform you that you have been made {{group_admin_label}} of {{group_name}} on {{site_name}}. You can manage your group by logging in and accessing settings on the Group page.<br /><br />Regards.'),(8,'Group Manager Removal','You are no longer {{group_admin_label}} of {{group_name}}','Hello {{first_name}} {{last_name}},<br /><br />We wish to inform you that you are no longer {{group_admin_label}} of {{group_name}} on {{site_name}}. Consequently, you will not be able to access and manage group settings.<br /><br />Regards.'),(9,'Password Reset by Group Manager','Your password was reset successfully','Hello {{first_name}} {{last_name}},<br /><br />We wish to inform you that a {{group_admin_label}} on {{site_name}} has reset your password. Your new password is:<br /><br />{{user_pass}}<br /><br />You can now login using your new password.<br /><br />Regards.'),(10,'On Request Denial','Membership request not approved','Dear {{first_name}} {{last_name}},<br /><br />Sorry, Your membership request for {{group_name}} was not approved.<br /><br />Kind Regards.'),(11,'Membership Terminated','Membership Terminated','Hi {{first_name}} {{last_name}},<br /><br />Your membership for the group {{group_name}} has been terminated on {{site_name}}. You no longer will have access to private areas of the group.<br /><br />Regards.'),(12,'Membership Request','You have a new membership request','Dear {{group_admin_label}},<br /><br />You have received a new membership request for your {{group_name}} Group. You can approve or reject the request by visiting the Group management area.<br /><br />Kind Regards.');
